() => {
  // Criar input do tipo date
  const input = document.createElement("input");
  input.type = "date";

  // Esconde fora da tela mas mantÃ©m funcional
  input.style.position = "fixed";
  input.style.left = "-9999px";

  // Listener para salvar quando escolher a data
  input.onchange = (e) => {
    const selectedDate = e.target.value; // formato YYYY-MM-DD

    // Salvar no Flaxboll
    tools.functions.setVar({
      args: "",
      pass: {
        keyPath: ["sc.a3.iptsChanges.date"],
        value: [selectedDate]
      }
    });

    console.log("ðŸ“… Data escolhida:", selectedDate);

    // Remove depois de usar
    document.body.removeChild(input);
  };

  // Adiciona no body
  document.body.appendChild(input);

  // ForÃ§a clique pra abrir calendÃ¡rio
  input.click();
}
