1765997535930.oldValue:
async () => {
  // Lista de vari√°veis a verificar
  const requiredFields = [
    "sc.a5.editData.order.order",
    "sc.a5.editData.order.date",
    "sc.a5.editData.order.total",
    "sc.a5.editData.order.status",
  ];

  const getVal = (path) => tools.getCtData(path);

  const allFilled = requiredFields.every((path) => {
    const value = getVal(path);
    return value !== undefined && value !== null && value !== "";
  });

  // ‚õî SE FALTAR ALGUM CAMPO, PARA O FLUXO AQUI
  if (!allFilled) {
    console.log("‚ùå Existem campos obrigat√≥rios vazios.");
    return;
  }

  console.log("‚úî Todos os campos preenchidos! Atualizando produto...");

  // üî• INICIALIZA√á√ÉO FIREBASE (necess√°ria!)
  let fbInit = tools.getCtData("all.temp.fireInit");

  if (!fbInit) {
    const { initializeApp, getApps } = await import("firebase/app");
    const cfg = tools.getCtData("all.temp.fireConfig");

    fbInit = getApps().length ? getApps()[0] : initializeApp(cfg);

    tools.setData({
      path: "all.temp.fireInit",
      value: fbInit,
    });
  }

  // Importa Firestore
  const { getFirestore, doc, updateDoc } = await import("firebase/firestore");
  const db = getFirestore(fbInit);

  // ID do documento
  const docId = getVal("sc.a5.editData.order.docId");
  if (!docId) {
    console.log("‚ùå Nenhum docId encontrado.");
    return;
  }

  // Dados completos do produto
  const orderData = getVal("sc.a5.editData.order");

  try {
    // üîÑ Update no documento
    await updateDoc(doc(db, "ordersEcommerce", docId), orderData);

    console.log("‚úî Order atualizado com sucesso!");

    // üßπ Limpar dados depois do update
    tools.setData({
      path: "sc.a5.editData.order",
      value: {},
    });

    console.log("üßπ Vari√°veis limpas ap√≥s o update.");

    // ‚úÖ Redirecionar para a tela a5AdmOrders
    tools.goTo("a5AdmOrders");
  } catch (err) {
    console.log("üî• Erro ao atualizar:", err);
  }
};

