() => {
  const cart = tools.getCtData("sc.C4.forms.iptsChanges.products");

  console.log("üõí Carrinho recebido:", cart);

  if (!Array.isArray(cart)) {
    console.log("ERRO: cart n√£o √© array");
    return "ERROR";
  }

  if (cart.length === 0) {
    console.log("Carrinho vazio");
    return "R$ 0,00";
  }

  const parsePrice = (p) => {
    console.log("Lendo price bruto:", p);

    if (!p || typeof p !== "string") {
      console.log("price inv√°lido:", p);
      return 0;
    }

    // Limpa a string do pre√ßo
    const cleaned = p
      .replace("R$", "")        // remove "R$"
      .replace(/\s+/g, "")      // remove espa√ßos
      .replace(",", ".")        // transforma v√≠rgula em ponto
      .trim();

    const num = Number(cleaned);

    console.log("price limpo: " + cleaned + " | N√∫mero: " + num);

    return isNaN(num) ? 0 : num;
  };

  let total = 0;

  cart.forEach((item, index) => {
    console.log("Item " + index + ": ", item);

    const price = parsePrice(item.price);

    console.log("Pre√ßo convertido do item " + index + ": " + price);

    total += price;
  });

  const formatted = total.toLocaleString("pt-BR", {
    style: "currency",
    currency: "BRL",
  });

  console.log("TOTAL CALCULADO: " + total + " | Formatado: " + formatted);

  tools.functions.setVar({
    args: "",
    pass: {
      keyPath: ["sc.C4.forms.iptsChanges.totalPrice"],
      value: [formatted],
    },
  });

  return formatted;
}

