() => {
  // Pegamos o número para o slider
  const maxPriceArr = tools.getCtData("sc.C2.filters.maxPrice") ?? [0];
  const maxPrice = maxPriceArr[0]; // número puro

  const parsePrice = (priceStr) => {
    if (!priceStr) return 0;
    return Number(
      priceStr
        .replace("R$", "")
        .replace(/\./g, "")
        .replace(",", ".")
        .trim()
    );
  };

  const applyPriceFilter = () => {
    const fullList = tools.getCtData("sc.c1.list") || [];

    const filtered = fullList.filter(item => {
      const productPrice = parsePrice(item.price);
      return productPrice <= maxPrice;
    });

    tools.functions.setVar({
      args: "",
      pass: {
        keyPath: ["sc.c1.filteredList"],
        value: filtered
      }
    });

    tools.functions.setVar({
      args: "",
      pass: {
        keyPath: ["sc.C2.toggles.filteredList"],
        value: true
      }
    });
  };

  return (
    <div style={{ width: "100%", padding: 16 }}>
      <div style={{ marginBottom: 8, fontSize: 16, fontWeight: 600 }}>
        Filtrar por preço máximo:
      </div>

      <input
        type="range"
        min="0"
        max="1000"
        step="1"
        value={maxPrice} // <-- número puro para slider funcionar
        onChange={(e) => {
          const value = Number(e.target.value);

          tools.functions.setVar({
            args: "",
            pass: {
              keyPath: ["sc.C2.filters.maxPrice"],
              value: [value] // <-- salvamos como array no Flaxboll
            }
          });
        }}
        style={{ width: "100%" }}
      />

      <div style={{ marginTop: 8, fontSize: 14, color: "#444" }}>
        R$ {maxPrice}
      </div>

      <div
        style={{
          marginTop: 16,
          padding: "10px 16px",
          borderRadius: 8,
          background: "#2563eb",
          color: "white",
          textAlign: "center",
          fontWeight: 600,
          cursor: "pointer",
        }}
        onClick={applyPriceFilter}
      >
        Aplicar filtro
      </div>
    </div>
  );
}
