onKeyDown={async (e) => {
  if (e.key === "Enter") {
    const label = tools.getCtData("sc.a2.selectedLabel");

    try {
      // ğŸ”¥ Executa a where normalmente
      const result = await tools.query.where(
        "productsEcommerce",
        "label",
        "==",
        label
      );

      // ğŸ“Œ Salva o resultado encontrado
      tools.setData({
        path: "sc.a2.filteredList",
        value: result,
      });

      // ğŸ“¦ Fecha o listBox
      tools.setData({
        path: "sc.A2.listBox",
        value: false,
      });

      console.log("ğŸ” Produto filtrado:", result);

    } catch (err) {
      console.log("âŒ Erro ao filtrar:", err);
    }
  }
}}

