export default function Botao() {
  const [hover, setHover] = useState(false);

  return (
    <Pressable
      onHoverIn={() => setHover(true)}
      onHoverOut={() => setHover(false)}
      style={{
        backgroundColor: hover ? '#333' : '#555',
        padding: 12,
        borderRadius: 6,
      }}
    >
      <Text style={{ color: 'white' }}>Passe o mouse aqui</Text>
    </Pressable>
  );
}
