1767212835483.oldValue:
() => {
  const storedDate = tools.getCtData("sc.a3.iptsChanges.date");

  // Garante que sempre receba uma string
  let dateValue = "";
  if (Array.isArray(storedDate)) {
    dateValue = storedDate[0] || "";
  } else if (typeof storedDate === "string") {
    dateValue = storedDate;
  }

  return (
    <div className="relative w-full">
      <input
        type="date"
        // value={dateValue}  ‚Üê coloque se quiser a data preenchida ao abrir
        onChange={(e) => {
          const selectedDate = e.target.value;

          tools.functions.setVar({
            args: "",
            pass: {
              keyPath: ["sc.a3.iptsChanges.date"],
              value: [selectedDate],
            },
          });

          console.log("üìÖ Data escolhida:", selectedDate);
        }}
        className="
          w-full
          px-4 py-3
          border border-gray-200
          rounded-xl
          shadow-sm
          text-gray-700
          placeholder-gray-400
          focus:outline-none
          focus:ring-2 focus:ring-blue-500
          focus:border-blue-500
          bg-white
          appearance-none
        "
      />

      {/* √çcone (opcional) */}
      <span className="absolute right-3 top-1/2 -translate-y-1/2 text-gray-400 pointer-events-none">
      </span>
    </div>
  );
};

