1757336599467.newValue:
() => <RN.FlatList
  data={tools.getCtData("sc.a3products.images")}
  numColumns={3}
  renderItem={({ item }) => (
    <RN.TouchableOpacity
      onPress={() => {
        tools.functions.setVar({
          args: "",
          pass: {
            keyPath: ["sc.products.selectedImage"],
            value: item
          }
        });
      }}
    >
      <RN.Image
        source={{ uri: item }}
        style={{ width: 100, height: 100, margin: 5, borderRadius: 8 }}
      />
    </RN.TouchableOpacity>
  )}
  keyExtractor={(item, index) => index.toString()}
/>

1757336599468.oldValue:
() => {

const storage = tools.functions.getStorage();

const imagesRef = ref(storage, "products");

listAll(imagesRef)
  .then(async (res) => {
    // Para cada item (arquivo), pegar a URL de download
    const urls = await Promise.all(res.items.map((itemRef) => getDownloadURL(itemRef)));

    // Aqui você já tem todas as URLs das imagens
    console.log("Imagens:", urls);

    // Salva no Flaxboll (para exibir no app)
    tools.functions.setVar({
      args: "",
      pass: {
        keyPath: ["sc.a3products.images"],
        value: urls
      }
    });
  })
  .catch((error) => {
    console.error("Erro ao listar imagens:", error);
})
}

