//========

const debugChars = (str) => {
  const chars = [...str].map(c => ({
    char: c,
    code: c.charCodeAt(0),
    hex: "U+" + c.charCodeAt(0).toString(16).toUpperCase()
  }));
  console.log("DEBUG CHAR MAP:", chars);
};


const parsePrice = (p) => {
  console.log("=== Iniciando parsePrice ===");
  console.log("Valor bruto recebido (raw):", JSON.stringify(p));
  debugChars(p);

  if (!p || typeof p !== "string") {
    console.log("‚ùå price inv√°lido:", p);
    return 0;
  }

  // Normaliza unicode
  p = p.normalize("NFKC");
  console.log("Ap√≥s normaliza√ß√£o NFKC:", JSON.stringify(p));

  // Remove "R$" e espa√ßos
  let cleaned = p.replace(/R\$\s*/gi, "").trim();
  console.log("Ap√≥s remover R$ e espa√ßos:", cleaned);

  // Agora s√≥ remove caracteres estranhos (mant√©m d√≠gitos, v√≠rgula, ponto)
  cleaned = cleaned.replace(/[^\d.,]/g, "");
  console.log("Ap√≥s remover caracteres n√£o num√©ricos:", cleaned);

  if (!cleaned || cleaned === "." || cleaned === ",") {
    console.log("‚ùå Nada limpou, retornando 0");
    return 0;
  }

  // Detecta formato
  if (cleaned.includes(",")) {
    console.log("Detectado formato brasileiro");
    cleaned = cleaned.replace(/\./g, ""); // remove pontos de milhar
    cleaned = cleaned.replace(",", ".");  // v√≠rgula vira ponto decimal
  } else {
    console.log("Detectado formato americano");
    cleaned = cleaned.replace(/,/g, "");  // remove v√≠rgulas de milhar
  }

  console.log("String final antes da convers√£o:", cleaned);
  const num = Number(cleaned);

  console.log("N√∫mero convertido:", num);
  return isNaN(num) ? 0 : num;
};


