  const list = tools.getCtData("sc.a2.list");

  if (!list || !Array.isArray(list)) {
    console.log("List not ready yet.");
    return;
  }

  tools.functions.setVar({
    args: "",
    pass: {
      keyPath: ["sc.a2.originalList"],
      value: [list]
    }
  });

  console.log("Original list saved.");
}
