const parsePrice = (p) => {
  console.log("=== Iniciando parsePrice ===");
  console.log("Valor bruto recebido:", JSON.stringify(p));

  if (!p || typeof p !== "string") {
    console.log("‚ùå price inv√°lido:", p);
    return 0;
  }

  // Remove "R$" e QUALQUER tipo de whitespace unicode
  let cleaned = p
    .replace(/R\$/gi, "")
    .replace(/\p{White_Space}/gu, "") // remove NBSP, NO-BREAK SPACE, etc.
    .trim();

  console.log("Ap√≥s remover R$ e espa√ßos unicode:", cleaned);

  // Remove tudo exceto n√∫meros, v√≠rgula e ponto
  cleaned = cleaned.replace(/[^\d.,]/g, "");
  console.log("Ap√≥s remover caracteres n√£o num√©ricos:", cleaned);

  // Formatos BR vs US
  if (cleaned.includes(",")) {
    console.log("Detectado formato brasileiro");
    cleaned = cleaned.replace(/\./g, "");
    cleaned = cleaned.replace(",", ".");
  } else {
    console.log("Detectado formato americano");
    cleaned = cleaned.replace(/,/g, "");
  }

  console.log("String final antes da convers√£o:", cleaned);
  const num = Number(cleaned);

  console.log("N√∫mero convertido:", num);
  return isNaN(num) ? 0 : num;
};


