1752261135435.oldValue:
() => {
  const email = tools.getCtData("sc.adm.login.forms.email");
  const password = tools.getCtData("sc.adm.login.forms.password");
  const users = tools.getCtData("sc.adm.login.db.users") || [];

  if (!email || email.trim() === "") {
    console.log("Por favor, preencha o e-mail.");
    return;
  }

  if (!password || password.trim() === "") {
    console.log("Por favor, preencha a senha.");
    return;
  }

  const userMatch = users.find(
    (user) => user.userEmail === email && user.userPassword === password
  );

  if (!userMatch) {
    console.log("E-mail ou senha incorretos.");
    return;
  }

  // Salva o usuário logado na sessão
  const sessionPath = "sc.adm.session.user";
  const sessionData = { keyPath: [sessionPath], value: userMatch };
  tools..setVar({ args: "", pass: sessionData });

  console.log("Login OK");

  tools.goTo("admDash");
}

