1767817144854.oldValue:
() => {
  const storedDate = tools.getCtData("sc.a3.iptsChanges.date");

  // For√ßa extrair sempre uma string
  let dateValue = "";
  if (Array.isArray(storedDate)) {
    dateValue = storedDate[0] || "";
  } else if (typeof storedDate === "string") {
    dateValue = storedDate;
  }

  const inputStyle = {
    paddingVertical: 16,
		paddingHorizontal: 15,
    borderRadius: 6,
    border: "1px solid #E6E7E8", // gray-300
    boxShadow: "0 1px 2px rgba(0,0,0,0.05)",
    transition: "all 0.2s",
    color: "#374151", // gray-700
    backgroundColor: "#fff",
    outline: "none",
    width: "180px"
  };

  const wrapperStyle = {
    position: "relative",
    width: "fit-content"
  };

  const iconStyle = {
    position: "absolute",
    right: "12px",
    top: "50%",
    transform: "translateY(-50%)",
    color: "#9ca3af", // gray-400
    pointerEvents: "none"
  };

  return (
    <div style={wrapperStyle}>
      <input
        type="date"
        //value={dateValue}
        onChange={(e) => {
          const selectedDate = e.target.value;

          tools.functions.setVar({
            args: "",
            pass: {
              keyPath: ["sc.a3.iptsChanges.date"],
              value: [selectedDate],
            },
          });

          console.log("üìÖ Data escolhida:", selectedDate);
        }}
        style={inputStyle}
      />
      <span style={iconStyle}></span>
    </div>
  );
}

