1757080558489.oldValue:
() => {
  const input = document.createElement("input");
  input.type = "file";
  input.accept = "image/*"; // or "*" for any file

  input.onchange = (e) => {
    const file = e.target.files[0];
    if (!file) {
      console.log("No file selected.");
      return;
    }

    // ✅ Create a temporary preview URL
    const previewUrl = URL.createObjectURL(file);

    // ✅ Save to two different vars
    tools.functions.setVar({
      args: "",
      pass: {
        keyPath: ["sc.a3.localFile", "sc.a3.previewUrl"], // array of paths
        value: [file, previewUrl] // array of values
      }
    });

    console.log("File selected:", file);
    console.log("Preview URL:", previewUrl);
  };

  input.click();
}

1757080558489.newValue:
() => {
  const input = document.createElement("input");
  input.type = "file";
  input.accept = "image/*"; // or "*" for any file

  input.onchange = (e) => {
    const file = e.target.files[0];
    if (!file) {
      console.log("No file selected.");
      return;
    }

    // ✅ Create a temporary preview URL
    const previewUrl = URL.createObjectURL(file);

    // Save file object
    tools.functions.setVar({
      args: "",
      pass: {
        keyPath: ["sc.a3.localFile"],
        value: [file]
      }
    });

    // Save preview URL
    tools.functions.setVar({
      args: "",
      pass: {
        keyPath: ["sc.a3.previewUrl"],
        value: [previewUrl]
      }
    });

    console.log("File selected:", file);
    console.log("Preview URL:", previewUrl);
  };

  input.click();
}
