( ) => {
const handleFileUpload = async () => {
  try {
    // 1. Pick a file from device
    const pickerResult = await DocumentPicker.pickSingle({
      type: [DocumentPicker.types.images, DocumentPicker.types.pdf],
      copyTo: 'cachesDirectory'
    });

    // 2. Get file details
    const { uri, name, type } = pickerResult;

    // 3. Create Firebase Storage reference
    const storageRef = storage().ref(`uploads/${name}`);

    // 4. Upload file to Firebase
    const task = storageRef.putFile(uri, {
      contentType: type
    });

    // 5. Monitor upload progress
    task.on('state_changed', 
      snapshot => {
        const progress = (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
        console.log(`Upload is ${progress}% done`);
      },
      error => {
        console.error('Upload failed:', error);
      },
      () => {
        // Upload completed successfully
        storageRef.getDownloadURL().then(url => {
          console.log('File available at:', url);
          // Handle success (e.g., save URL to database)
        });
      }
    );

  } catch (err) {
    if (DocumentPicker.isCancel(err)) {
      console.log('User cancelled the picker');
    } else {
      console.error('Error:', err);
    }
  }
}
 }

const ImageUploader = () => {
  const [image, setImage] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [transferred, setTransferred] = useState(0);
  const fileInputRef = useRef(null);

  // For web, we'll use a hidden input element
  if (Platform.OS === 'web') {
    return (
      <View>
        <input
          ref={fileInputRef}
          type="file"
          accept="image/*"
          style={{ display: 'none' }}
          onChange={handleWebFileChange}
        />
        <Button
          title="Select Image"
          onPress={() => fileInputRef.current.click()}
        />
        {image && (
          <>
            <Image 
              source={{ uri: image.uri }} 
              style={{ width: 200, height: 200, marginVertical: 20 }} 
            />
            <Button
              title={uploading ? "Uploading..." : "Upload Image"}
              onPress={uploadImage}
              disabled={uploading}
            />
          </>
        )}
        {uploading && (
          <View style={{ marginVertical: 20 }}>
            <ActivityIndicator size="large" />
            <Text>{transferred}% Completed</Text>
          </View>
        )}
      </View>
    );
  }

  // For mobile, we'll use ImagePicker (built-in)
  const pickImage = async () => {
    // For mobile, we'll need to use ImagePicker which is part of React Native
    // Note: This is a simplified approach - in a real app, you might need to handle permissions
    try {
      const result = await DocumentPicker.getDocumentAsync({
        type: 'image/*',
        copyToCacheDirectory: true,
      });

      if (!result.cancelled) {
        setImage(result);
      }
    } catch (err) {
      Alert.alert('Error', 'Failed to pick image');
    }
  };

  const uploadImage = async () => {
    if (!image) {
      Alert.alert('Error', 'Please select an image first');
      return;
    }

    setUploading(true);
    setTransferred(0);

    try {
      // Create a reference to the location you want to upload to
      const filename = image.uri.split('/').pop();
      const reference = storage().ref(`images/${filename}`);

      // Upload the file
      const task = reference.putFile(image.uri);

      // Track upload progress
      task.on('state_changed', taskSnapshot => {
        const progress = (taskSnapshot.bytesTransferred / taskSnapshot.totalBytes) * 100;
        setTransferred(Math.round(progress));
      });

      // Wait for upload to complete
      await task;

      // Get the download URL
      const downloadUrl = await reference.getDownloadURL();

      Alert.alert('Success', 'Image uploaded successfully!');
      console.log('Download URL:', downloadUrl);
      setImage(null);
    } catch (error) {
      console.error('Upload error:', error);
      Alert.alert('Error', 'Failed to upload image');
    } finally {
      setUploading(false);
    }
  };

  const handleWebFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      setImage({
        uri: URL.createObjectURL(file),
        name: file.name,
        type: file.type,
        size: file.size,
      });
    }
  };

  return (
    <View style={{ padding: 20 }}>
      <Button title="Select Image" onPress={pickImage} />

      {image && (
        <>
          <Image 
            source={{ uri: image.uri }} 
            style={{ width: 300, height: 300, marginVertical: 20 }} 
          />
          <Button
            title={uploading ? "Uploading..." : "Upload Image"}
            onPress={uploadImage}
            disabled={uploading}
          />
        </>
      )}

      {uploading && (
        <View style={{ marginTop: 20 }}>
          <ActivityIndicator size="large" />
          <Text>{transferred}% Completed</Text>
        </View>
      )}
    </View>
  );
};

