(txt) => {
  try {
    if (typeof txt !== "string") txt = String(txt ?? "");

    // Mantm apenas nmeros (sem regex)
    let clean = "";
    for (let i = 0; i < txt.length; i++) {
      const ch = txt[i];
      if (ch >= "0" && ch <= "9") clean += ch;
    }

    // Remove zeros  esquerda (mas deixa pelo menos um)
    clean = clean.replace(/^0+/, "");
    if (clean.length === 0) clean = "0";

    // Constri centavos e separadores
    let intPart = clean.slice(0, -2);
    let cents = clean.slice(-2);

    // Se tiver s um dgito, considera como "0X"
    if (clean.length === 1) {
      intPart = "0";
      cents = "0" + clean;
    }

    // Se tiver dois dgitos,  "XX" => 0,XX
    if (clean.length === 2) {
      intPart = "0";
      cents = clean;
    }

    // Formata milhares manualmente (sem regex)
    let intFormatted = "";
    let counter = 0;

    for (let i = intPart.length - 1; i >= 0; i--) {
      intFormatted = intPart[i] + intFormatted;
      counter++;
      if (counter === 3 && i > 0) {
        intFormatted = "." + intFormatted;
        counter = 0;
      }
    }

    const masked = "R$ " + intFormatted + "," + cents;

    tools.functions.setVar({
      args: "",
      pass: {
        keyPath: ["sc.a3.iptsChanges.price"],
        value: [String(masked)],
      },
    });
  } catch (e) {
    console.error("Erro na mscara BRL:", e);
    return txt;
  }
}

