() => {
  // 1. Pega o produto
  const product = tools.getCtData("sc.C2.forms.iptsChanges");
  if (!product) return;

  // 2. Pega o carrinho atual
  let currentCart = tools.getCtData("sc.C4.forms.iptsChanges.products") || [];

  // Se for objeto ao invs de array, corrigir
  if (!Array.isArray(currentCart)) {
    currentCart = [currentCart];
  }

  // 3. Atualiza o carrinho
  const updatedCart = [...currentCart, product];

  // 4. Salva como ARRAY dentro de ARRAY (FlaxBoll exige isso)
  tools.functions.setVar({
    args: "",
    pass: {
      keyPath: ["sc.C4.forms.iptsChanges.products"],
      value: [updatedCart],  // <- formato 100% compatvel com listas
    },
  });

  console.log(" Produto adicionado ao carrinho:", product);
  console.log(" Carrinho atualizado:", updatedCart);

  // 5. Navega para o carrinho
  tools.goTo("c4cart");
}

() => {
  const cart = tools.getCtData("sc.C4.forms.iptsChanges.products");

  console.log(" Carrinho recebido:", cart);

  if (!Array.isArray(cart)) {
    console.log("ERRO: cart no  array");
    return "ERROR";
  }

  if (cart.length === 0) {
    console.log("Carrinho vazio");
    return "R$ 0,00";
  }

  const parsePrice = (p) => {
    console.log("Lendo price bruto:", p);

    if (!p || typeof p !== "string") {
      console.log("price invlido:", p);
      return 0;
    }

    // Limpa a string do preo
    const cleaned = p
      .replace("R$", "")        // remove "R$"
      .replace(/\s+/g, "")      // remove espaos
      .replace(",", ".")        // transforma vrgula em ponto
      .trim();

    const num = Number(cleaned);

    console.log("price limpo: " + cleaned + " | Nmero: " + num);

    return isNaN(num) ? 0 : num;
  };

  let total = 0;

  cart.forEach((item, index) => {
    console.log("Item " + index + ": ", item);

    const price = parsePrice(item.price);

    console.log("Preo convertido do item " + index + ": " + price);

    total += price;
  });

  const formatted = total.toLocaleString("pt-BR", {
    style: "currency",
    currency: "BRL",
  });

  console.log("TOTAL CALCULADO: " + total + " | Formatado: " + formatted);

  tools.functions.setVar({
    args: "",
    pass: {
      keyPath: ["sc.C4.forms.iptsChanges.totalPrice"],
      value: [formatted],
    },
  });

  return formatted;
}

